package ru.sbtqa.pft.sandbox;public class MyFirstProgram {  public static void main(String[] args) {    hello("world");    hello("user");    hello("Anastasia");    Square s = new Square(8);    Rectangle r = new Rectangle(5,4);    System.out.println("Площадь квадрата со стороной " + s.a + " = " + s.area());    System.out.println("Площадь прямоугольника со сторонами " + r.a + " и " + r.b + " = " + r.area(r.a, r.b));  }  public static void hello(String name) {    System.out.println("Hello, " + name);  }}