package ru.sbtqa.pft.sandbox;public class MyFirstProgram {  public static void main(String[] args) {    hello("world");    hello("user");    hello("Anastasia");    double l = 8;    double a = 5;    double b = 4;    System.out.println("Площадь квадрата со стороной " + l + " = " + area(l));    System.out.println("Площадь прямоугольника со сторонами " + a + " и " + b + " = " + area(a, b));  }  public static void hello(String name) {    System.out.println("Hello, " + name);  }  public static double area(double a) {    return a * a;  }  public static double area(double a, double b) {    return a * b;  }}